mapper.update:
UPDATE MEMBER
SET MEMBER_NICKNAME = #{ nickname }
WHERE MEMBER_EMAIL = #{ memberName }

UPDATE MEMBER
SET MEMBER_PHONE = #{ phone }
WHERE MEMBER_EMAIL = #{ memberName }

UPDATE MEMBER
SET MEMBER_PASSWORD = #{ encpw }
WHERE MEMBER_EMAIL = #{ memberName }

UPDATE MEMBER
SET MEMBER_STATUS = 'y'
WHERE MEMBER_EMAIL = #{ memberName }

UPDATE MEMBER
SET MEMBER_AUTH = 's'
WHERE MEMBER_EMAIL = #{ memberName }

UPDATE MEMBER
SET MEMBER_AUTH = 'c'
WHERE MEMBER_EMAIL = #{ memberName }

update orders
set request_status = #{ requestStatus }
where order_no = #{ orderNo }

mapper.insert:
INSERT INTO guide_file(
                        g_file_path
                        ,g_file_name
                        ,order_no
                        )
VALUES (
        #{ guideFilePath }
        ,#{ guideFileName }
        ,#{ orderNo }
        )

update orders
set request_status = #{ requestStatus }
    ,order_finaldt = #{ orderFinalDt }
where order_no = #{ orderNo }

update orders
set request_status = #{ requestStatus }
    ,order_final = #{ orderFinal }
where order_no = #{ orderNo }

mapper.select:
select count(*)
from orders
where port_no = #{ portNo }

select count(*)
from orders
where member_no = #{ memberNo }
