mapper.insert:
INSERT
INTO PORTFOLIO (
                PORT_NO
                , PORT_TITLE
                , PORT_CON
                , PORT_CONTACTDT
                , MEMBER_INFO
                , MEMBER_NO
                , WRITE_DT
)
VALUES (
        #{ portNo }
        , #{ portTitle }
        , #{ portCon }
        , #{ portContactDt }
        , #{ memberInfo }
        , #{ memberNo }
        , now()
)

INSERT
INTO PORT_FILE (
                FILE_NO
                , PORT_NO
                , FILE_PATH
                , FILE_NAME
                , FILE_TYPE
                , THUMB_PATH
)
VALUES (
         #{ fileNo }
        , #{ refPortNo }
        , #{ filePath }
        , #{ fileName }
        , #{ fileType }
        , #{ thumbnailPath }
)

INSERT
INTO PORT_OPTION (
                    OPTION_NO
                    , PORT_NO
                    , OPTION_PRICE
                    , OPTION_INFO
                    , OPTION_FIX
                    , OPTION_DT
)
VALUES (
        #{ optionNo }
        , #{ portNo }
        , #{ optionPrice }
        , #{ optionInfo }
        , #{ optionFix }
        , #{ optionDt }
)
